let mobileNavTrigger=document.querySelector(".mobile-nav-trigger");let nav=document.querySelector('.main-navigation .nav');function mobileNavOpen(e){if(!nav.classList.contains('mobile-nav-is-open')){nav.classList.add('mobile-nav-is-open')}else{nav.classList.remove('mobile-nav-is-open')}}
mobileNavTrigger.addEventListener('click',mobileNavOpen);document.addEventListener('scroll',animate);function animate(e){let about=document.querySelector('.about');if(window.scrollY>about.offsetTop-200){let bars=document.querySelectorAll('.bar .bar');for(let i=0;i<bars.length;i++){bars[i].classList.add('fill')}}
let work=document.querySelector('.work');let firstProject=document.querySelector('.first-project');let secondProject=document.querySelector('.second-project');let thirdProject=document.querySelector('.third-project');let fourthProject=document.querySelector('.fourth-project');if(window.innerWidth>960){if(window.scrollY>work.offsetTop-200){firstProject.classList.add('animated','fadeInLeft')}
if(window.scrollY>firstProject.offsetTop-200){secondProject.classList.add('animated','fadeInLeft')}
if(window.scrollY>secondProject.offsetTop-200){thirdProject.classList.add('animated','fadeInLeft')}
if(window.scrollY>thirdProject.offsetTop-200){fourthProject.classList.add('animated','fadeInLeft')}}}
$('a[href*="#"]').not('[href="#"]').not('[href="#0"]').click(function(event){if(location.pathname.replace(/^\//,'')==this.pathname.replace(/^\//,'')&&location.hostname==this.hostname){var target=$(this.hash);target=target.length?target:$('[name='+this.hash.slice(1)+']');if(target.length){event.preventDefault();$('html, body').animate({scrollTop:target.offset().top},1000,function(){var $target=$(target);$target.focus();if($target.is(":focus")){return!1}else{$target.attr('tabindex','-1');$target.focus()}})}}})